const dropdownInit=()=>{document.addEventListener("click",e=>{let t;e.target.closest("[data-dropdown]")&&(t=e.target.closest("[data-dropdown]")).classList.toggle("active"),document.querySelectorAll("[data-dropdown].active").forEach(e=>{e!==t&&e.classList.remove("active")})})};function burgerMenu(){const e=document.querySelector(".burger"),t=document.querySelector(".header__nav"),c=document.querySelector(".overlay");e.addEventListener("click",()=>{e.classList.toggle("active"),t.classList.toggle("active"),c.classList.toggle("active")}),c.addEventListener("click",()=>{e.classList.remove("active"),t.classList.remove("active"),c.classList.remove("active")}),window.addEventListener("resize",()=>{991.98<window.innerWidth&&(e.classList.remove("active"),t.classList.remove("active"),c.classList.remove("active"))})}burgerMenu(),dropdownInit();